changeSet.sql:
CREATE OR REPLACE FUNCTION calculate_user_xp(u_id BIGINT)
RETURNS INTEGER AS $$
BEGIN
RETURN (
    (SELECT COALESCE(COUNT(*), 0) * 100 FROM user_learning_path WHERE user_id = u_id) +
    (SELECT COALESCE(COUNT(*), 0) * 500 FROM certificates WHERE user_id = u_id)
    );
END;
$$ LANGUAGE plpgsql;
